import React from "react";

export default function Weather() {
  const [city, setCity] = React.useState("");
  const [cityWeather, setCityWeather] = React.useState(null);

  const handleSearch = () => {
    if (city) {
      fetchData(city);
    }
  };

  const apiKey = "a239b7d13dab53cb92a7c7c65c3dc402";
  const fetchData = (city) => {
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=${apiKey}`
    )
      .then((response) => response.json())
      .then((data) => {
        setCityWeather(data);
        console.log(data.main);
      });
  };

  return (
    <div className="weather">
      <div className="header">
        <i className="fa-solid fa-location-dot fa-2xl"></i>
        <input
          className="city-input"
          placeholder=""
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <button onClick={handleSearch}>
          <i className="fa-solid fa-magnifying-glass fa-2xl"></i>
        </button>
      </div>

      <img></img>
      <div className="weather-data">
        <span className="weather-celcius">
          {cityWeather ? cityWeather.main.temp : "?"}°C
        </span>
        <span className="feelslike">
          {cityWeather ? cityWeather.main.feels_like : "?"}°C
        </span>
        <span>
          {cityWeather ? cityWeather.weather[0].description : "feels like:"}
        </span>
      </div>
      <div className="footer">
        <div className="humidity weather-data-footer">
          {cityWeather && (
            <>
              <i class="fa-solid fa-water fa-2xl"></i>
              <div className="details">
                <span className="details-data">
                  {cityWeather.main.humidity}%
                </span>
                <span className="details-text">Humidity</span>
              </div>
            </>
          )}
        </div>

        <div className="wind weather-data-footer">
          <i class="fa-solid fa-wind fa-2xl"></i>
          <div className="details">
            {cityWeather && (
              <>
                <span className="details-data">
                  {cityWeather.wind.speed}m/s
                </span>
                <span className="details-text">Wind Speed</span>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
